@article{YangCPFEST,
  url = {http://arxiv.org/abs/2004.10093v1},
  title = {Curriculum Pre-training for End-to-End Speech Translation},
  authors = {Chengyi Wang, Yu Wu, Shujie Liu, Ming Zhou, Zhenglu Yang},
  abstract = {  End-to-end speech translation poses a heavy burden on the encoder, because it has to transcribe, understand, and learn cross-lingual semantics simultaneously. To obtain a powerful encoder, traditional methods pre-train it on ASR data to capture speech features. However, we argue that pre-training the encoder only through simple speech recognition is not enough and high-level linguistic knowledge should be considered. Inspired by this, we propose a curriculum pre-training method that includes an elementary course for transcription learning and two advanced courses for understanding the utterance and mapping words in two languages. The difficulty of these courses is gradually increasing. Experiments show that our curriculum pre-training method leads to significant improvements on En-De and En-Fr speech translation benchmarks. },
  bibkey = {YangCPFEST},
  arxiv_id = {2004.10093v1},
  subject = {cs.CL},
  submission_date = {2020-04-21T15:12:07Z}
}

@article{GuSRIEST,
  url = {http://arxiv.org/abs/2006.12124v2},
  title = {Self-Supervised Representations Improve End-to-End Speech Translation},
  authors = {Anne Wu, Changhan Wang, Juan Pino, Jiatao Gu},
  abstract = {  End-to-end speech-to-text translation can provide a simpler and smaller system but is facing the challenge of data scarcity. Pre-training methods can leverage unlabeled data and have been shown to be effective on data-scarce settings. In this work, we explore whether self-supervised pre-trained speech representations can benefit the speech translation task in both high- and low-resource settings, whether they can transfer well to other languages, and whether they can be effectively combined with other common methods that help improve low-resource end-to-end speech translation such as using a pre-trained high-resource speech recognition system. We demonstrate that self-supervised pre-trained features can consistently improve the translation performance, and cross-lingual transfer allows to extend to a variety of languages without or with little tuning. },
  bibkey = {GuSRIEST},
  arxiv_id = {2006.12124v2},
  subject = {eess.AS},
  submission_date = {2020-06-22T10:28:38Z}
}

@article{ZhuSAEITPMIST,
  url = {http://arxiv.org/abs/2105.05752v2},
  title = {Stacked Acoustic-and-Textual Encoding: Integrating the Pre-trained  Models into Speech Translation Encoders},
  authors = {Chen Xu, Bojie Hu, Yanyang Li, Yuhao Zhang, shen huang, Qi Ju, Tong Xiao, Jingbo Zhu},
  abstract = {  Encoder pre-training is promising in end-to-end Speech Translation (ST), given the fact that speech-to-translation data is scarce. But ST encoders are not simple instances of Automatic Speech Recognition (ASR) or Machine Translation (MT) encoders. For example, we find that ASR encoders lack the global context representation, which is necessary for translation, whereas MT encoders are not designed to deal with long but locally attentive acoustic sequences. In this work, we propose a Stacked Acoustic-and-Textual Encoding (SATE) method for speech translation. Our encoder begins with processing the acoustic sequence as usual, but later behaves more like an MT encoder for a global representation of the input sequence. In this way, it is straightforward to incorporate the pre-trained models into the system. Also, we develop an adaptor module to alleviate the representation inconsistency between the pre-trained ASR encoder and MT encoder, and develop a multi-teacher knowledge distillation method to preserve the pre-training knowledge. Experimental results on the LibriSpeech En-Fr and MuST-C En-De ST tasks show that our method achieves state-of-the-art BLEU scores of 18.3 and 25.2. To our knowledge, we are the first to develop an end-to-end ST system that achieves comparable or even better BLEU performance than the cascaded ST counterpart when large-scale ASR and MT data is available. },
  bibkey = {ZhuSAEITPMIST},
  arxiv_id = {2105.05752v2},
  subject = {cs.CL},
  submission_date = {2021-05-12T16:09:53Z}
}

